"""
Create a simple app icon using base64 encoded ICO data.
This creates a basic PDF download icon without external dependencies.
"""

import base64

# Simple 32x32 ICO file data (base64 encoded)
# This is a basic PDF icon with download arrow
ICO_DATA = """

"""

def create_app_icon():
    """Create a simple app icon."""
    try:
        # Decode and save the ICO data
        ico_bytes = base64.b64decode(ICO_DATA.strip())
        with open('app_icon.ico', 'wb') as f:
            f.write(ico_bytes)
        print("✅ Created app_icon.ico successfully!")
        return 'app_icon.ico'
    except Exception as e:
        print(f"❌ Error creating icon: {e}")
        # Create a simple placeholder
        with open('app_icon.ico', 'w') as f:
            f.write("# Placeholder icon\n")
        print("⚠️ Created placeholder icon file")
        return 'app_icon.ico'

if __name__ == "__main__":
    create_app_icon()
